<style scoped lang="scss">
	.content {
		background-image: url("data:image/jpeg;base64,");
		.top{
			padding-top: $uni-margin-tb;
			.adress{
				font-size: $uni-font-size-cg;
				color: $uni-text-color-inverse;
				height: $uni-height-l;
				line-height: $uni-height-l;
				.icon-dizhiguanli{
					padding: 0 10upx;
					font-size: $uni-font-size-lc;
				}
			}
			.now_watch{
				margin-top: $uni-margin-tc;
				margin-left: $uni-margin-tl;
				color: $uni-text-color-inverse;
				.temp{
					text{
						font-size: 120upx;
					}
					.degree{
						
					}
				}
				.inline{
					font-size: $uni-font-size-base;
					.item{
						height: $uni-height-s;
						line-height: $uni-height-s;
					}
				}
			}
		}
		.main{
			margin-top: $uni-margin-tb;
		}
		.bot{
			
		}
	}
	.uni-common-mt{
		position: fixed;
		height: 100vh;
		width: 100vw;
		top: 0;
		left: 0;
		background: #fff;
		display: flex;
		flex-wrap: wrap;  
		align-items: center;
		.uni-center{
			display: block;
			width: 100vw;
			text-align: center;
			margin-top: 30rpx;
			color: #999;
		}
	}
</style>

<template>
	<view>
		<view class="content">
			<scroll-view scroll-y="true" :style="{height:scollHeight+'px'}">
				<!-- 頭部 -->
				<view class="top">
					<view class="adress">
						<text class="iconfont icon-dizhiguanli"></text>
						<text @tap="cityList">{{city}}</text>
					</view>
					<view class="now_watch">
						<view class="temp">
							<text class="temp">{{now.tmp == undefined ? 0 : now.tmp}}°</text>
						</view>
						<view class="inline">
							<view class="item">{{now.cond_txt == undefined ? "" : now.cond_txt}} {{now.hum == undefined ? "" : now.hum}}</view>
							<view class="item">{{now.wind_dir == undefined ? "" : now.wind_dir}} {{now.wind_sc == undefined ? 0 : now.wind_sc}}级</view>
						</view>
					</view>
				</view>
				<view class="main">
					<view class="items" v-for="(item,index) in list" :key="index">
						<text></text>
						<text></text>
						<text></text>
					</view>
				</view>
				<!-- 底部 -->
				<view class="bot">
					
				</view>
			</scroll-view>
		</view>
		
		<view class="uni-common-mt" v-if="setting.gesture && pwState">
			<mpvue-gesture-lock :containerWidth="590" :cycleRadius="70" @end="onEnd"></mpvue-gesture-lock>
			<view class="uni-center">{{text}}</view>
		</view>
	</view>
	
</template>

<script >
	import {
		mapState,
		mapMutations
	} from "vuex"
	import {getWeather, getFuture, gelocation, getAir, getLift} from "@/common/utils/https.js"
	import mpvueGestureLock from '@/components/mpvueGestureLock/index.vue';
	//#ifdef MP-WEIXIN
		import amapFile from "@/common/utils/amap-wx.js"
	//#endif
	export default {
		data() {
			return {
				scollHeight:0,
				now:{},
				updata:{},
				list:[],
				local:{
					lat:"",
					lng:""
				},
				air:{},
				tips:"",
				text:"手势解锁"
			}
		},
		components: {
			mpvueGestureLock
		},
		computed: {
			...mapState({
				city: state => state.global.city,
				setting: state => state.global.setting,
				psWord: state => state.global.psWord,
				pwState: state => state.global.pwState
			})
		},
		onNavigationBarButtonTap() {  
			console.log("点击了自定义按钮");  
		}, 
		onLoad() {
			console.log("2")
			this.getSystemHeight()
		},
		onShow() {
			//#ifdef H5 
				document.title="首頁"
			//#endif
				
			this.getLocation()
			
		},
		onReady() {
			console.log("4")
		},
		onBackPress(options) {
			console.log('from:' + options.from)
		},
		onHide() {
			console.log("yc")
		},
		onUnload() {
			console.log("xz")
		},
		onPullDownRefresh() {
			this.getWether(this.city)
		},
		created: function() {
			console.log("1")
		},
		mounted: function() {
			console.log("3")
		},
		beforeDestroy: function() {
			console.log("8")
		},
		destroyed: function() {
			console.log("9")
		},
		methods: {
			...mapMutations(["setPwSta"]),
			// 手势解锁
			onEnd(data) {
				console.log(data.join(''))
				if(data.join('') === this.psWord) {
					this.setPwSta(false)
					console.log("解锁成功")
				}else{
					this.text = "手势不正确"
				}
			},
			// 高德地址逆解析
			getLocation() {
				// 仅支持https
				uni.getLocation({
					success: function (res) {
						console.log(res)
					}
				})
				this.getWether(this.city)
			},
			// 获取页面数据
			getWether(key) {
				console.log("-------------------" , key)
				var that = this
				if(!this.setting.gesture) {
					this.$tos.Loding("加載中...")
				}
				getWeather(key).then((res) =>{
					if(res.result.HeWeather6[0].status === "ok") {
						that.local.lat = res.result.HeWeather6[0].basic.lat
						that.local.lng = res.result.HeWeather6[0].basic.lon
						that.now = res.result.HeWeather6[0].now
						that.now.hum = that.$tos.humidity(that.now.hum)
					}else{
						throw res.result
					}
				}).then(()=>{
					return getFuture(key).then((res) =>{
						console.log("2哈" , res)
						if ( res.result.HeWeather6[0].status === "ok") {
							that.list = res.result.HeWeather6[0].daily_forecast
						} else{
							throw res.result.reason
						}
					})
				}).then(()=>{
					//#ifdef H5 
						return gelocation(that.local).then((res) =>{
							console.log("3哈" , res)
							if ( res.result.resultcode === "200" ) {
								that.adress = res.result.result.ext.district
								console.log(that.adress)
							} else{
								throw res.result.reason
							}
						})
					//#endif
					
					//#ifdef MP-WEIXIN 
						console.log("4哈")
					//#endif
				}).catch((e) => {
					console.log(e, "catch" )
					that.$tos.tip(e)
				}).finally(() =>{
					console.log("finally")
					uni.stopPullDownRefresh();
					that.$tos.HideLoding()
				})
			},
			// 跳转城市列表
			cityList() {
				uni.navigateTo({
					url: "/pages/list/list"
				})
			},
			//動態獲取可視區域高度
			getSystemHeight() {
				var that = this
				uni.getSystemInfo({
					success(res) {
						console.log(res)
						that.scollHeight = res.windowHeight
					},
				})
			}
		}
	}
</script>